mapper.select:
SELECT * FROM `ADVERTISE` a LEFT JOIN ROOM r ON a.id = r.adv_id LEFT JOIN BOOK b ON r.id = b.room_id WHERE a.id IN ( SELECT r.adv_id FROM ROOM r WHERE r.id NOT IN ( SELECT b.room_id FROM BOOK b LEFT JOIN ROOM r ON r.id = b.room_id WHERE b.stay_end >= #{stayBegin,jdbcType=TIMESTAMP} and b.stay_begin <= #{stayEnd,jdbcType=TIMESTAMP} GROUP BY b.room_id ) GROUP BY r.adv_id HAVING count( * ) >= #{room.adv.bedroom} ) AND a.id IN ( SELECT r.adv_id FROM ROOM r WHERE r.id NOT IN ( SELECT b.room_id FROM BOOK b LEFT JOIN ROOM r ON r.id = b.room_id WHERE b.stay_end >= #{stayBegin,jdbcType=TIMESTAMP} and b.stay_begin <= #{stayEnd,jdbcType=TIMESTAMP} GROUP BY b.room_id ) GROUP BY r.adv_id HAVING sum( r.max_people ) >= #{room.adv.maxPeople}
)
and address like CONCAT('%',#{room.adv.city},'%')
and property_type like #{room.adv.propertyType}
and adv_type like #{room.adv.advType}
GROUP BY a.id
